#!/bin/bash
set -o errexit

yq --inplace '(.services) += (.services | with_entries(select((.value | has("hostname")) and (.value.annotations | has("dapr.io/enabled")))) | with_entries(. as $e | .key |= . + "-sidecar"| .value |= { "image": "daprio/daprd:latest", "command": [ "./daprd", ("--app-id=" + ($e.value.annotations."dapr.io/app-id" // error("missing app-id annotation"))), ("--app-port=" + ($e.value.annotations."dapr.io/app-port" // "")), ("--enable-api-logging=" + ($e.value.annotations."dapr.io/enable-api-logging" // "false")), "--placement-host-address=placement:50006", "--resources-path=/components" ], "network_mode": ("service:" + $e.key), "volumes": [".score-compose/mounts/components/:/components"], "depends_on": ["placement"] }))' compose.yaml